import path from "path";
import webpack from "webpack";
import HtmlWebpackPlugin from "html-webpack-plugin";
import ForkTsCheckerWebpackPlugin from "fork-ts-checker-webpack-plugin";
import { Configuration as WebpackConfig } from "webpack";
import { Configuration as DevServerConfig } from "webpack-dev-server";

interface Configuration extends WebpackConfig {
  devServer?: DevServerConfig;
}

const isProd = process.env.NODE_ENV === "production";
const repoBase = isProd ? "/anime-foliage/" : "/";

const config: Configuration = {
  entry: "./src/index.tsx",
  output: {
    path: path.resolve(__dirname, "dist"),
    filename: "main.js",
    publicPath: repoBase,           // ← here
    clean: true,
  },
  resolve: { extensions: [".tsx", ".ts", ".js"] },
  module: {
    rules: [
      {
        test: /\.tsx?$/,
        use: [
          {
            loader: 'ts-loader',
            options: {
              transpileOnly: true
            }
          },
          {
            loader: "babel-loader",
            options: {
              presets: [
                ["@babel/preset-env", {
                  "modules": false
                }],
                ["@babel/preset-react", {
                  runtime: "automatic"
                }],
                "@babel/preset-typescript"
              ],
              plugins: [
                ["babel-plugin-reactylon"]
              ]
            }
          },
        ],
        exclude: '/node_modules/',
      },
      {
        test: /\.css$/i,
        use: [
          'style-loader',
          'css-loader',
        ],
      },
      {
        test: /\.(png|jpg|jpeg|glb|gltf|wasm)$/i, // ✅ Add glb/gltf/wasm
        type: 'asset/resource'
      },
    ],
  },
  plugins: [
    new webpack.DefinePlugin({
      "process.env.PUBLIC_URL": JSON.stringify(repoBase.slice(0, -1)),
    }),
    new HtmlWebpackPlugin({
      template: "public/index.html",
      templateParameters: {
        BASE_HREF: repoBase     // ← pass it here
      }
    }),
    new ForkTsCheckerWebpackPlugin(),
    new webpack.ProvidePlugin({
      process: "process/browser"
    })
  ],
  devServer: {
    static: path.join(__dirname, "public"),
    hot: true,
    historyApiFallback: true,
    port: 3000
  }
};

export default config;
